import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import DonutChart from '../DonutChart/DonutChart';
import {makeData} from '../Utility/DataHandling';
import './summary.css';
import {SettingsAlert} from '../Utility/Alert';
import { connect } from 'http2';


class Summary extends Component {
  constructor(props){
    super(props);
    this.state={
      data:{},
      showWarning:false
    }
  }


  componentWillMount(){
    this.setState({
      data:makeData(1.5,0.2)
    })

    this.loadData = setInterval(() => {
      const data = makeData(1.5,0.2);
      this.setState({
        data:data
      })
    }, 1000);
  }

  componentWillUnmount(){
    clearInterval(this.loadData);
  }

  render() {
    // const test = this.state.data
    // console.log(test[test.length-1].value);

    return (
      <div id='summary'>
            <SettingsAlert showWarning={this.state.showWarning}/>
            <div id='charts'>
              <div id="line-chart">
                <LineChart
                    width={800} 
                    height={400}
                    data = {this.state.data}
                    range={[0,3]}
                    title={'Overall Energy Consumption'}
                />
              </div>
              <div id='donut-chart'><DonutChart /></div>
            </div>

            <h2 className='headline'>Temperature Status</h2>
            <div id='rooms'>
              <div className='room'>
                Kitchen
                <h5>Temperature: {this.props.kitchenCurrentTemp}</h5>
                <h6>Status:cooling</h6>
              </div>

              <div className='room'>
                Bedroom
                <h5>Temperature: 26Â°F </h5>
                <h6>Status:cooling</h6>
              </div>
              <div className='room'>
                Living Room
                <h5>Temperature: 26C</h5>
                <h6>Status:cooling</h6>
              </div>
              <div className='room'>
                Bathroom
                <h5>Temperature: 26C</h5>
                <h6>Status:cooling</h6>
              </div>

            </div>

      </div>
    )
  }
}

const mapStateToProps=state=>{
  return{
    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp
  }
}

export default connect(mapStateToProps)(Summary);
