import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import {connect} from 'react-redux';
import {makeData} from '../Utility/DataHandling';
import * as d3 from "d3";
import {
  changeBathroomTemp,
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomStatus,
  setKitchenStatus,
  setBathroomStatus,
  setBedroomStatus,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
} from '../store/actions/temperatureSettingAction';

class Room extends Component {
  constructor(props){
    super();
  }

  componentWillMount(){
    const {
      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,

      setLivingroomTempData,
      setBedroomTempData,
      setBathroomTempData,
      setKitchenTempData,

    } = this.props;
    //prefill data
    setLivingroomTempData(makeData(livingroomCurrentTemp,0.2));
    setBedroomTempData(makeData(bedroomCurrentTemp,0.2));
    setBathroomTempData(makeData(bathroomCurrentTemp,0.2));
    setKitchenTempData(makeData(kitchenCurrentTemp,0.2));
  }

  componentDidMount(){
    const {
      roomName, 

      kitchenTemperature,
      livingroomTemperature,
      bathroomTemperature,
      bedroomTemperature,

      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,

      livingroomStatus,
      bedroomStatus,
      bathroomStatus,
      kitchenStatus,

      livingroomTempData,
      bedroomTempData,
      bathroomTempData,
      kitchenTempData,


      changeBathroomTemp, 
      changeLivingroomTemp,
      changeKitchenTemp,
      changeBedroomTemp,

      setLivingroomStatus,
      setKitchenStatus,
      setBathroomStatus,
      setBedroomStatus,

      setLivingroomTempData,
      setBedroomTempData,
      setBathroomTempData,
      setKitchenTempData,


    } = this.props;

    const temp =(function(){
      if(roomName==="Kitchen")  return {kitchenTemperature,kitchenCurrentTemp};
      if(roomName==="Livingroom") return {livingroomTemperature,livingroomCurrentTemp};
      if(roomName==="Bedroom") return {bedroomTemperature, bedroomCurrentTemp};
      if(roomName==="Bathroom") return {bathroomTemperature, bathroomCurrentTemp};
    })();

    var {tempTarget, currentTemp} = temp;
    tempTarget = typeof tempTarget === "number"? tempTarget: tempTarget.temperature;
    currentTemp = typeof currentTemp === "number"? currentTemp: currentTemp.temperature;

    const tempDiff = tempTarget - currentTemp;

    //set room status: cooling/heating/idle
    if(tempDiff>0){
      this.setState({heating: true, cooling:false, idle:false})
    }else if(tempDiff <0){
      this.setState({heating: false, cooling:true,idle:false})
    }else{
      this.setState({heating: false, cooling:false,idle:true})
    };


    this.showTempInterval = setInterval(()=>{
      if(currentTemp === tempTarget){
        if(roomName==="Kitchen") {
          var kitchenData = this.state.kitchenData;
          kitchenData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          kitchenData.shift();

          this.setState({
            kitchenData:kitchenData
          })
        }
        if(roomName==="Bedroom") {
          var bedroomData = this.state.bedroomData;
          bedroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bedroomData.shift();

          this.setState({
            bedroomData:bedroomData
          })
        }

        if(roomName==="Bathroom") {
          var bathroomData = this.state.bathroomData;
          bathroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bathroomData.shift();

          this.setState({
            bathroomData:bathroomData
          })
        }
        if(roomName==="Livingroom") {
          var livingroomData = this.state.livingroomData;
          livingroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          livingroomData.shift();

          this.setState({
            livingroomData:livingroomData
          })
        }

      }},1000)

    this.changeTempInterval = setInterval(()=>{
      if(currentTemp!==tempTarget){
        if(roomName==="Kitchen") {
          console.log('wrong???')
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeKitchenTemp(currentTemp);
          console.log('currentTemp',currentTemp)

          var kitchenData = this.state.kitchenData;

          // console.log('kitchenData',kitchenData)
          kitchenData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          kitchenData.shift();
          console.log('kitchenData',kitchenData)
          this.setState({
            kitchenData:kitchenData
          })
          console.log("this.state.kitchenData",this.state.kitchenData);
        }
        if(roomName==="Bedroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeBedroomTemp(currentTemp);
          var bedroomData = this.state.bedroomData;
          bedroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bedroomData.shift();
          this.setState({
            bedroomData:bedroomData
          })
        }
        if(roomName==="Bathroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeBathroomTemp(currentTemp);
          var bathroomData = this.state.bathroomData;
          bathroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bathroomData.shift();
          this.setState({
            bathroomData:bathroomData
          })
        }
        if(roomName==="Livingroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeLivingroomTemp(currentTemp);
          var livingroomData = this.state.livingroomData;
          livingroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          livingroomData.shift();
          this.setState({
            livingroomData:livingroomData
          })
        }

        tempTarget = Math.round(tempTarget*10)/10;

        if(currentTemp === tempTarget){
          clearInterval(this.changeTempInterval);
        }
      }
    },1000)

  }

  componentWillUnmount(){
    clearInterval(this.changeTempInterval);
    clearInterval(this.showTempInterval);
  }




  render() {
    const {roomName,type} =this.props;
    const {
      kitchenData,
      bedroomData,
      livingroomData,
      bathroomData,
      cooling,
      heating,
      idle
    } = this.state;


    var data =(function(){
      if(roomName==="Kitchen") return kitchenData;
      if(roomName==="Livingroom") return livingroomData;
      if(roomName==="Bedroom") return bedroomData;
      if(roomName==="Bathroom")return bathroomData;
    })();
    var Temperature = data[data.length-1].value;
    Temperature = Math.round(Temperature*100)/100;
    return (
      <div className='room'>
          <h1>{roomName}</h1>
          <h5>Temperature: {Temperature}Â°F </h5>
          <h6>Status:cooling</h6>

          <LineChart 
              type={type}
              width={600} 
              height={400}
              title={'Current Temperature'}
              data = {data}
              range={[65,90]}
          />
      </div>
    )
  }
}

const mapStateToProps=state=>{

  return{
    kitchenTemperature: state.temperature.kitchenTemperature,
    livingroomTemperature: state.temperature.livingroomTemperature,
    bedroomTemperature: state.temperature.bedroomTemperature,
    bathroomTemperature: state.temperature.bathroomTemperature,

    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp,

    livingroomStatus:state.temperature.livingroomStatus,
    bedroomStatus:state.temperature.bedroomStatus,
    bathroomStatus:state.temperature.bathroomStatus,
    kitchenStatus:state.temperature.kitchenStatus,

    livingroomTempData:state.temperature.livingroomTempData,
    bedroomTempData:state.temperature.bedroomTempData,
    bathroomTempData:state.temperature.bathroomTempData,
    kitchenTempData:state.temperature.kitchenTempData,

  }
}

export default connect(mapStateToProps, {
  changeBathroomTemp, 
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomStatus,
  setKitchenStatus,
  setBathroomStatus,
  setBedroomStatus,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
})(Room);
